import { Component, Input, Output, EventEmitter } from '@angular/core';

import { Saving } from '../../saving.model';
import { SavingService } from '../../savings.service';

@Component({
    selector: 'app-update-saving-modal',
    templateUrl: './update-saving-modal.component.html',
    styleUrls: ['./update-saving-modal.component.scss']
})
export class UpdateSavingModalComponent {
    @Input() saving!: Saving;
    @Input() types!: string[];
    @Output() updatedSaving = new EventEmitter<Saving>();
    @Output() close = new EventEmitter<void>();

    constructor(private savingService: SavingService) { }

    update(): void {
        this.savingService.update(this.saving).subscribe(
            () => {
                this.updatedSaving.emit(this.saving);
                this.close.emit();
            },
            err => console.error(err.error.message)
        );
    }

    cancel(): void {
        this.close.emit();
    }
}
