import { Component, EventEmitter, Input, Output } from '@angular/core';

import { OperationService } from '../../operation.service';
import { Operation } from '../../operation.model';

@Component({
    selector: 'app-delete-operation-modal',
    templateUrl: './delete-operation-modal.component.html',
    styleUrls: ['./delete-operation-modal.component.scss']
})
export class DeleteOperationModalComponent {
    @Input() operation!: Operation;
    @Output() remove = new EventEmitter<void>();
    @Output() close = new EventEmitter<void>();

    constructor(private operationService: OperationService) { }

    deleteAndUpdateSaving(): void {
        this.operationService.delete({ id: this.operation.id, updateSaving: true }).subscribe(
            () => this.remove.emit(),
            err => console.error(err.error.message),
            () => this.close.emit()
        );
    }

    delete(): void {
        this.operationService.delete({ id: this.operation.id, updateSaving: false }).subscribe(
            () => this.remove.emit(),
            err => console.error(err.error.message),
            () => this.close.emit()
        );
    }

    cancel(): void {
        this.close.emit();
    }
}
