import { Component, Input, Output, EventEmitter } from '@angular/core';

import { Category } from '../../category.model';
import { CategoryService } from '../../categories.service';

@Component({
    selector: 'app-update-category-modal',
    templateUrl: './update-category-modal.component.html',
    styleUrls: ['./update-category-modal.component.scss']
})
export class UpdateCategoryModalComponent {
    @Input() category!: Category;
    @Output() updatedCategory = new EventEmitter<Category>();
    @Output() close = new EventEmitter<void>();

    constructor(private categoryService: CategoryService) { }

    update(): void {
        this.categoryService.update(this.category).subscribe(
            () => {
                this.updatedCategory.emit(this.category);
                this.close.emit();
            },
            err => console.error(err.error.message)
        );
    }

    cancel(): void {
        this.close.emit();
    }
}
