import { Component } from '@angular/core';

@Component({
    selector: 'app-settings',
    templateUrl: './settings.page.html',
    styleUrls: ['./settings.page.scss']
})
export class SettingsPage {
    updateProfileActive: boolean = true;
    updatePasswordActive: boolean = false;

    constructor() { }

    openUpdateProfile(): void {
        this.updateProfileActive = true;
        this.updatePasswordActive = false;
    }

    openUpdatePassword(): void {
        this.updateProfileActive = false;
        this.updatePasswordActive = true;
    }
}
