import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, throwError } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { Router } from '@angular/router';

import { environment } from 'src/environments/environment'; 
import { SavingService } from 'src/app/modules/savings/savings.service';
import { NotifyService } from 'src/app/shared/services/notify.service';
import { RegistrationDto } from './dto/registration.dto';
import { LoginDto } from './dto/login.dto';
import { User } from 'src/app/modules/user/user.model';

@Injectable({
    providedIn: 'root'
})
export class AuthService {
    private readonly url = environment.apiUrl + 'auth/';
    private readonly currentUserKey = 'currentUser';
    currentUser$: BehaviorSubject<User | null> = 
        new BehaviorSubject<User | null>(JSON.parse(localStorage.getItem(this.currentUserKey)!));;

    constructor(
        private http: HttpClient,
        private router: Router,
        private savingService: SavingService,
        private notifyService: NotifyService,
    ) { }

    get currentUser(): User | null {
        return this.currentUser$.getValue();
    }

    setCurrentUser(user: User | null): void {
        localStorage.setItem(this.currentUserKey, JSON.stringify(user));
        this.currentUser$.next(user);
    }

    login(loginDto: LoginDto): Observable<void> {
        return this.http.post<User>(this.url + 'login', loginDto).pipe(
            map(user => {
                this.setCurrentUser(user);
                this.savingService.getBalance().subscribe();
                this.notifyService.showSuccess('Logged in!');
                this.router.navigate(['/operations']);
            }),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }

    logout(): void {
        localStorage.removeItem(this.currentUserKey);
        this.currentUser$.next(null);
        this.savingService.balance$.next(null);
        this.router.navigate(['/auth']);
    }

    register(registrationDto: RegistrationDto): Observable<void> { 
        return this.http.post<User>(this.url + 'register', registrationDto).pipe(
            map(user => {
                this.setCurrentUser(user);
                this.savingService.getBalance().subscribe();
                this.notifyService.showSuccess('Account successfully registrated!');
                this.router.navigate(['/operations']);
            }),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }
}
