import { Component, EventEmitter, Input, Output } from '@angular/core';

import { CategoryService } from '../../categories.service';
import { Category } from '../../category.model';

@Component({
    selector: 'app-delete-category-modal',
    templateUrl: './delete-category-modal.component.html',
    styleUrls: ['./delete-category-modal.component.scss']
})
export class DeleteCategoryModalComponent {
    @Input() category!: Category;
    @Output() remove = new EventEmitter<void>();
    @Output() close = new EventEmitter<void>();

    constructor(private categoryService: CategoryService) { }

    deleteWithOperationsAndUpdateSavings(): void {
        this.categoryService.delete({
            id: this.category.id,
            deleteOperations: true,
            updateSavings: true
        }).subscribe(
            () => this.remove.emit(),
            err => console.error(err.error.message),
            () => this.close.emit()
        );
    }

    deleteWithOperations(): void {
        this.categoryService.delete({
            id: this.category.id,
            deleteOperations: true,
            updateSavings: false
        }).subscribe(
            () => this.remove.emit(),
            err => console.error(err.error.message),
            () => this.close.emit()
        );
    }

    delete(): void {
        this.categoryService.delete({
            id: this.category.id,
            deleteOperations: false,
            updateSavings: false
        }).subscribe(
            () => this.remove.emit(),
            err => console.error(err.error.message),
            () => this.close.emit()
        );
    }

    cancel(): void {
        this.close.emit();
    }
}
