import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { map, catchError } from 'rxjs/operators';

import { environment } from 'src/environments/environment';
import { AuthService } from 'src/app/modules/auth/auth.service';
import { NotifyService } from 'src/app/shared/services/notify.service';
import { User } from './user.model';
import { UpdateUserDto } from './dto/update-user.dto';
import { UpdatePasswordDto } from './dto/update-password.dto';

@Injectable({
    providedIn: 'root'
})
export class UserService {
    private readonly url = environment.apiUrl + 'users/';

    constructor(
        private http: HttpClient,
        private authService: AuthService,
        private notifyService: NotifyService,
    ) { }

    updateUser(userDto: UpdateUserDto): Observable<void> {
        return this.http.put<User>(this.url, userDto).pipe(
            map((user: User) => {
                const token = this.authService.currentUser!.token;
                this.authService.setCurrentUser({ ...user, token });
                this.notifyService.showSuccess('User successfully updated!')
            }),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }

    updatePassword(passwordDto: UpdatePasswordDto): Observable<void> {
        return this.http.put<void>(this.url + 'password', passwordDto).pipe(
            map(() => this.notifyService.showSuccess('Password successfully updated!')),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }
}
