import { Component, Output, EventEmitter} from '@angular/core';
import { Validators, FormControl, FormGroup } from '@angular/forms';

import { CreateCategoryDto } from '../../dto/create-category.dto';
import { CreateCategory } from '../../interfaces/create-category.interface';

@Component({
    selector: 'app-create-category-modal',
    templateUrl: './create-category-modal.component.html',
    styleUrls: ['./create-category-modal.component.scss']
})
export class CreateCategoryModalComponent {
    @Output() cateoryToCreate = new EventEmitter<CreateCategory>();
    @Output() close = new EventEmitter<void>();

    createCategoryForm = new FormGroup({
        name: new FormControl('', Validators.compose([
            Validators.required,
            Validators.minLength(4),
            Validators.maxLength(12),
            Validators.pattern('^[a-zA-Z0-9]+$'),
        ]))
    });

    get name() { return this.createCategoryForm.get('name'); }

    constructor(private categoryService: CategoryService) { }

    create(): void {
        this.categoryService.create(this.createCategoryForm.value as CreateCategoryDto).subscribe(
            category => {
                this.createdCategory.emit(category);
                this.close.emit();
            },
            err => console.error(err.error.message)
        );
    }

    cancel(): void {
        this.close.emit();
    }
}
