import { Component, OnInit } from '@angular/core';

import { SavingService } from 'src/app/modules/savings/saving.service';
import { CategoryService } from 'src/app/modules/categories/category.service';
import { Operation } from '../../operation.model';
import { Saving } from 'src/app/modules/savings/saving.model';
import { Category } from 'src/app/modules/categories/category.model';
import { OperationService } from '../../operation.service';
import { OperationFilterDto } from '../../dto/operation.filter.dto';
import { PageEvent } from 'src/app/shared/interfaces/page-event.interface';

@Component({
  selector: 'app-operations',
  templateUrl: './operations.page.html',
  styleUrls: ['./operations.page.scss']
})
export class OperationsPage implements OnInit {
    operations: Operation[] = [];
    types: string[] = ['Incoming', 'Spend'];
    categories: Category[] = [];
    savings: Saving[] = [];

    filter: OperationFilterDto = {
        type: null,
        categoryId: null,
        savingId: null,
    };
    pageIndex = 0;
    pageSize = 5;
    pageSizeOptions: number[] = [5, 10, 25, 100];
    operationsLength = 0;

    createMode = false;
    editMode = false;
    deleteMode = false;
    operationToUpdate: Operation | null = null;
    operationToDelete: Operation | null = null;

    constructor(
        private operationService: OperationService,
        private categoryService: CategoryService,
        private savingService: SavingService
    ) { }

    applyFilter(filter: OperationFilterDto): void {
        this.filter = filter;
        this.getOperations();
    }

    applyPage(event: PageEvent): void {
        this.pageIndex = event.pageIndex;
        this.pageSize = event.pageSize;
        this.getOperations();
    }

    getOperations(): void {
        this.operationService.get({
            page: this.pageIndex + 1,
            limit: this.pageSize,
            ...this.filter
        }).subscribe(
            data => {
                this.operations = data.rows;
                this.operationsLength = data.count;
            },
            err => console.error(err.error.message)
        );
    }

    addToOperations(operation: Operation): void {
        this.operations.unshift(operation);
    }

    replaceOperation(operation: Operation): void {
        const operationToUpdate = this.operations.find(item => item.id === operation.id)!;
        const index = this.operations.indexOf(operationToUpdate);
        this.operations.splice(index, 1, operation);
    }

    removeOperation(): void {
        this.operations = this.operations.filter(operation =>
            operation.id !== this.operationToDelete!.id);
    }

    enableCreateMode(): void {
        this.createMode = true;
    }

    disableCreateMode(): void {
        this.createMode = false;
    }

    enableEditMode(operation: Operation): void {
        this.editMode = true;
        this.operationToUpdate = JSON.parse(JSON.stringify(operation));
    }

    disableEditMode(): void {
        this.editMode = false;
        this.operationToUpdate = null;
    }

    enableDeleteMode(operation: Operation): void {
        this.deleteMode = true;
        this.operationToDelete = JSON.parse(JSON.stringify(operation));
    }

    disableDeleteMode(): void {
        this.deleteMode = false;
        this.operationToDelete = null;
    }

    ngOnInit(): void {
        this.getOperations();

        this.savingService.get(null).subscribe(
            data => this.savings = data,
            err => console.error(err.error.message)
        );

        this.categoryService.get().subscribe(
            data => this.categories = data,
            err => console.error(err.error.message)
        );
    }
}
