import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, map } from 'rxjs/operators';

import { environment } from 'src/environments/environment'; 
import { NotifyService } from 'src/app/shared/services/notify.service';
import { Category } from './category.model';
import { CreateCategoryDto } from './dto/create-category.dto';
import { UpdateCategoryDto } from './dto/update-category.dto';
import { DeleteCategoryDto } from './dto/delete-saving.dto';

@Injectable({
    providedIn: 'root'
})
export class CategoryService {
    private readonly url = environment.apiUrl + 'categories/';

    constructor(
        private http: HttpClient,
        private notifyService: NotifyService,
    ) { }

    create(dto: CreateCategoryDto): Observable<Category> {
        return this.http.post<Category>(this.url, dto).pipe(
            map(category => {
                this.notifyService.showSuccess('Category successfully created!');
                return category;
            }),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }

    get(): Observable<Category[]> {
        return this.http.get<Category[]>(this.url).pipe(
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }

    update(dto: UpdateCategoryDto): Observable<void> {
        return this.http.put<void>(this.url, dto).pipe(
            map(() => this.notifyService.showSuccess('Category successfully updated!')),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }

    delete(dto: DeleteCategoryDto): Observable<void> {
        return this.http.delete<void>(this.url, {
            params: JSON.parse(JSON.stringify(dto))
        }).pipe(
            map(() => this.notifyService.showSuccess('Category successfully deleted!')),
            catchError(err => {
                this.notifyService.showError(err.error.message);
                return throwError(err);
            })
        );
    }
}
